## ä»£ç ï¼š

```cpp
#include<bits/stdc++.h>
using namespace std;
//4
//0100
int main() {
    int t; cin >> t;
    while(t--) {
        int n; cin >> n;
        bool in;
        string res = '0';
        //ç¬¬ä¸€æ¬¡æ“ä½œï¼š
        cout << '? ' << res << endl;
        cin >> in;
        if(!in) res = '1';
        if(n == 1) {
            cout << '! ' << res << endl;
            continue;
        }
        //æ£€æŸ¥åç¼€
        bool zero, one;
        while(1) {

            cout << '? ' << res << '0' << endl;
            cin >> zero;

            cout << '? ' << res << '1' << endl;
            cin >> one;

            if((!zero && !one)) {
                break;
            }
            else if(zero) {
                res += '0';
            }
            else res += '1';
            if(res.size() == n) break;
        }
        int size = res.size();
        while(size < n) {

            cout << '? ' << '0' << res  << endl;
            cin >> zero;

            if(zero) {
                res = '0' + res;
            }
            else res = '1' + res;
            size++;
        }

        cout << '! ' << res << endl;
        cout.flush();
    }
}
