```cpp
#include<bits/stdc++.h>
using namespace std;

void work() {
    int n; cin >> n;
    vector<vector<int>> res;
    for(int i = 1; i <= n; i++) {
        if(1 < i) {
            res.push_back({i, 1, i});
        }
        if(i + 1 < n) {
            res.push_back({i, i + 1, n});
        }
    }
    cout << res.size() <<endl;
    for(vector<int> r : res) {
        cout << r[0] << ' ' << r[1] << ' ' << r[2] << endl;
    }
}

int main() {
    int t; cin >> t;
    while(t--) work();
    return 0;
}
