```cpp
#include<bits/stdc++.h>
using namespace std;
#define int long long
void solution() {
    int n; cin >> n;
    vector<int> res(2 * n + 1);
    for(int i = 0; i < 2 * n; i++) {
        cin >> res[i + 1];
    }
    sort(res.begin() + 1, res.end(), [](int a, int b) {return a > b;});
    int ans = res[1] + res[2 * n];
    for(int i = 2; i < 2 * n - 1; i += 2) {
        ans += res[i] - res[i + 1];
    }

    cout << res[1] << ' ' << ans << ' ';
    for(int i = 2; i< 2 * n + 1; i++) {
        cout << res[i] << ' \n'[i == 2 * n];
    }
}

signed main() {
    int t; cin >> t;
    while(t--) solution();
    return 0;
}
