using namespace std;

int main() {
    int n, m, s, e, val;
    cin >> n >> m;

	//ç”¨æ¥å­˜æ”¾è¾¹ä¸å…¶æƒå€¼çš„æ•°ç»„
    vector<vector<int>> grid;
    for(int i = 0; i < m; i++) {
        cin >> s >> e >> val;
        grid.push_back({s, e, val});
    }

	//å­˜æ”¾è·ç¦»åŸç‚¹çš„æœ€çŸ­æ€»è·ç¦»
